catalog_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below
    var account_id = getParameterValue('account_name');
    if (account_id) {
        g_form.setValue('account', account_id);
        var gRef = g_form.getReference('account', SetValues);
        alert(gRef.getDisplayValue());
        g_form.setValue('city', gRef.city);
    }


}


function getParameterValue(name) {
    name = name.replace(/[\[]/, "\\\[").replace(/[\]]/, "\\\]");
    var regexS = "[\\?&]" + name + "=([^&#]*)";
    var regex = new RegExp(regexS);
    var results = regex.exec(top.location);



    if (results == null) {
        return "";
    } else {
        return unescape(results[1]);


    }

}

function SetValues(gRef) {
    alert(gRef);
    g_form.setValue('city', gRef.city);
    g_form.setValue('zip_code', gRef.zipcode);
    g_form.setValue('state', gRef.state);

}
